 //Dynamic 2D Array#include<stdio.h>int i, n, j, testCases;int l1, l2, r1, r2;int d[10][10]; // Dynamic sum arrayint a[10][10]; // Our 2D gridint main () {        scanf("%d", &n); // Getting the grid size i.e. 3x3        // Filling the array    for (i = 1 ; i <= n; i++) {        for (j = 1 ; j <= n; j++)            scanf("%d", &a[i][j] );    }        dynamicGrid();    //    scanf("%d", &testCases);//    //    for (i = 0 ; i <= n; i++) {//        printf("Enter the test case %i\n", i);//        scanf("%d %d", &l1, %r1);//        //    }        scanf("%d %d", &l1, %r1);        for (i = 0 ; i <= n ; i++) {        if(d[i][j]==d[])    }        }dynamicGrid(){       // First: Filling the first row    for (i = 1 ; i<= n ; i++)    d[1][i] = d[1][i-1] + a[1][i];        // Second: Filling the first column    for (i = 1 ; i <= n; i++)    d[i][1] = d[i-1][1] + a[i][1];        // For the rest we will use first column and row    for (i = 2 ; i <= n; i++)    for (j = 2 ; j <= n; j++)    d[i][j] = d[i-1][j] + d[i][j-1] - d[i-1][j-1] + a[i][j];        // Printing the summed array    for (i = 1 ; i <= n; i++) {        for (j = 1 ; j <= n; j++)            printf("%d ", d[i][j] );        printf("\n");    }}