#include<iostream>#include<vector>#include<stdio.h>#define INF 999999using namespace std;int main () {        int n, m;    // Given a dataset    cin >> n >> m; // Node, way        int arr[n][n];        for (int i = 0; i < n; ++i)        for (int j = 0; j < n; ++j)            arr[i][j] = INF;        for (int i = 0; i < n; ++i) {        int a, b, c;        cin >> a >> b >> c;        arr[b][a] = arr[a][b] = c;    }        // Algo    for (int k = 0; k < n; ++k)        for (int i = 0; i < n; ++i)            for (int j = 0; j < n; ++j)                if ( i != j )                    if (arr[i][j] > arr[i][k] + arr[k][j])                        arr[i][j] = arr[i][k] + arr[k][j];    //    // Distance between st - fin    int st, fin;    cin >> st >> fin;    cout << arr[st][fin] << endl;    }