#include <iostream>#include <cstring>using namespace std;int t, i, j, r, c, a = 1, strt;char charr[10001];char goal[] = {'A','L','L','I','Z','Z','W','E','L','L'};bool was[1001], done;bool ways[101][101];void BFS(int curr_loc, int a) {        was[curr_loc] = true;    if (a == 10) { done = true; }    // cout << "a = " << a << " charr[curr_loc] = " << charr[curr_loc] << " curr_loc = " << curr_loc << endl;        for (i = 1; i <= c*r; ++i) {        if (ways[curr_loc][i] && !was[i] && charr[i] == goal[a]) {            BFS(i, a+1);            a--;            i=1;        }    }}int main() {        cin >> t;    while(t--){                done = false, a = 1 ;        memset (charr , ' ' , sizeof (charr));        memset (was , false, sizeof (was));        cin >> r >> c;                for (i = 1; i <= r*c; ++i){            cin >> charr[i];            ways[i][i+c] = true, ways[i][i-c] = true, ways[i][i+c+1] = true;            ways[i][i+c-1] = true, ways[i][i-c+1] = true, ways[i][i-c-1] = true;            if(i % c != 0) ways[i][i+1] = true;            if(i % c != 1) ways[i][i-1] = true;        }        BFS(1, a);                if (done) cout << "YES" << endl;        else cout << "NO" << endl;    }        return 0;}