// Dijkstra: Shortest Reach 2// https://www.hackerrank.com/challenges/dijkstrashortreach#include <stdio.h>#include <string.h>#include <math.h>#include <stdlib.h>#include <stdbool.h>#define inf pow(2,31)-1int t,min;int n,m,i,a,b,j,counter = 1,k,s;int d[3001],was[3001];int main () {        int array[1001][1001] = {0};    for(i=0 ; i<3001 ; i++){        d[i]=inf;was[i]=false;    }        scanf("%d", &t);        while(t--){                scanf("%d %d", &n, &m);                for (i=0; i<m ; i++) {            scanf("%d %d %d", &a,&b,&k);            array[a][b]=k;            array[b][a]=k;        }        scanf("%d", &s);        d[s]=0;                for(i=1 ; i<=n ; i++){            min = inf;            for(j=1 ; j<=n ; j++){                if(d[j]<min && was[j]==false){                    min = d[j];                    s = j;                }            }                        was[s]=true;                        for(j=1 ; j<=n ; j++){                if(array[s][j]>0 && d[s]+array[s][j]<d[j]){                    d[j]=d[s]+array[s][j];                }            }        }                for(i=2 ; i<=n ; i++){            if(d[i]==inf){                printf("-1 ");            }else{                printf("%d ", d[i]);            }        }    }}